mapper.select:
select mem.user_id, password, name, email, phone_num, address, auth
from member mem left outer join auth a on mem.user_id = a.user_id
where mem.user_id = #{user_id}

select user_id, person_num1, person_num2
from member
where name = #{name} and person_num1 = #{person_num1}

select password, person_num2
from member
where name = #{name} and person_num1 = #{person_num1} and user_id = #{user_id}

mapper.update:
update member
set password = #{password}
where user_id = #{user_id}
